struct MandelBookmark : Hashable
{
    std::string data;

    GLuint thumb_tex = 0;

    MandelBookmark() {}
    MandelBookmark(
        std::string_view _data)
    {
        data = _data;

        invalidate_hash();
    }

    bool loadFromThumbnail(std::string_view webp_path)
    {
        blPrint() << "loadFromThumbnail: " << webp_path;

        std::ifstream in(webp_path.data(), std::ios::binary);
        if (!in)
            return false;

        std::string txt;
        in.seekg(0, std::ios::end);
        txt.resize((std::size_t)in.tellg());
        in.seekg(0, std::ios::beg);
        in.read(txt.data(), (std::streamsize)txt.size());

        bytebuf webp_data = bytebuf(txt.data(), txt.data() + txt.size());
        webp_extract_save_from_xmp(webp_data, data);

        return true;
    }

    //void serialize(JSON::json& j) const
    //{
    //    j = JSON::json{{"data", data}};
    //}
    //
    //bool deserialize(const JSON::json& j)
    //{
    //    // Required fields
    //    auto read_string = [&](const char* key, std::string& out) -> bool {
    //        auto it = j.find(key);
    //        if (it == j.end() || !it->is_string()) return false;
    //        out = it->get<std::string>();
    //        return true;
    //    };
    //
    //    if (!read_string("data", data)) return false;
    //
    //    invalidate_hash();
    //
    //    return true;
    //}

    hash_t compute_hash() const noexcept override
    {
        return Hasher::hash_string(data);
    }

    std::string thumbFilename()
    {
        return compression::b64_encode_u64(stable_hash()) + ".webp";
    }

    std::string thumbPath()
    {
        return ProjectBase::activeProject()->root_path("data/bookmarks/" + thumbFilename());
    }

    void loadThumbnail()
    {
        if (thumb_tex == 0)
            thumb_tex = loadGLTextureRGBA8(thumbPath().c_str());
    }

    void loadThumbnail(std::string_view path)
    {
        blPrint() << "loadThumbnail: " << path;
        if (thumb_tex == 0)
            thumb_tex = loadGLTextureRGBA8(path.data());
    }

    void loadThumbnail(const bytebuf& data, int w, int h)
    {
        thumb_tex = createGLTextureRGBA8(data.data(), w, h);
    }

    int thumbTexture()
    {
        //if (thumb_tex == 0)
        //    thumb_tex = loadGLTextureRGBA8(platform()->path(thumb_path).c_str());
        
        return thumb_tex;
    }
};

